package org.Arashio.switchsis;

public class SwitchSisters {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Asashio sisters:");
		switch(4) {
			case 1:
				System.out.println("Asashio");
				break;
			case 2:
				System.out.println("Oshio");
				break;
			case 3:
				System.out.println("Michishio");
				break;
			case 4:
				System.out.println("Arashio");
				break;
			case 5:
				System.out.println("Asagumo");
				break;
			case 6:
				System.out.println("Yamagumo");
				break;
			case 7:
				System.out.println("Natsugumo");
				break;
			case 8:
				System.out.println("Minegumo");
				break;
			case 9:
				System.out.println("Arare");
				break;
			case 10:
				System.out.println("Kasumi");
				break;
			default:
				System.out.println("Number input error!");
				break;
		}
		

	}

}
