package org.Arashio.variable;

public class Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a;
		a = 2;
		System.out.println(a*100); //200
		System.out.println(a*314); //628
		
		a = 5;
		System.out.println(a*100); //500
		System.out.println(a*314); //1570
		
		int b = 47;
		System.out.println(a-b); //42
		System.out.println(b/a); //9
		System.out.println(a+a+a); //15
		
		double r = 3325;
		System.out.println(r/29);
		System.out.println(100.0/3);

	}

}
